import { useEffect, useState } from "react";
import EmployeeService from "../Services/EmployeeService";
import { useNavigate, useParams } from "react-router-dom";

const CreateOrUpdateEmployee = () => {
    const navigate = useNavigate();
    const {id} = useParams();
    const [firstName,setFirstName] = useState("");
    const [lastName,setLastName] = useState("");
    const [email,setEmail] = useState("");
    const changeFirstNameHandler = (event) => {
        setFirstName(event.target.value)
    }
    const changeLastNameHandler = (event) => {
        setLastName(event.target.value);
    }
    const changeEmailHandler = (event) => {
        setEmail(event.target.value);
    }
    const saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        const employee = {firstName: firstName,lastName:lastName,email:email};
        console.log(employee);
        if(id < 0){
            EmployeeService.createEmployee(employee).then(()=>{
                navigate("/");
            });
        } else {
            EmployeeService.updateEmployee(id,employee).then(()=>{
                navigate("/");
            });
        }
    }
    const cancel = () => {
        navigate("/")
    }
    useEffect(()=>{
        if(id < 0) {
            return;
        }
        EmployeeService.getEmployeeById(id).then(res => {
            const employee = res.data;
            setFirstName(employee.firstName);
            setLastName(employee.lastName);
            setEmail(employee.email);
        }).catch(err => console.log(err));
    },[]);

    return(
        <>
        <div className="container" style={{ marginTop: "4rem" }}>
    <div className="row">
        <div className="card col-md-6 offset-md-3">
            <h3 className="text-center">{id < 0 ? "Add" : "Update"} Employee</h3>
            <div className="card-body">
                <form className="form" onSubmit={saveOrUpdateEmployee}>
                    <div className="form-group">
                        <label>First Name</label>
                        <input 
                            type="text" 
                            placeholder="First Name" 
                            name="firstName" 
                            className="form-control" 
                            value={firstName} 
                            onChange={changeFirstNameHandler} 
                        />
                    </div>
                    <div className="form-group">
                        <label>Last Name</label>
                        <input 
                            type="text" 
                            placeholder="Last Name" 
                            name="lastName" 
                            className="form-control" 
                            value={lastName} 
                            onChange={changeLastNameHandler} 
                        />
                    </div>
                    <div className="form-group">
                        <label>Email</label>
                        <input 
                            type="text" 
                            placeholder="Email" 
                            name="email" 
                            className="form-control" 
                            value={email} 
                            onChange={changeEmailHandler} 
                        />
                    </div>
                    <div className="button-group">
                        <button type="submit" className="btn btn-success">Save</button>
                        <button type="button" className="btn btn-danger" onClick={cancel}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>

        </>
    );
}
export default CreateOrUpdateEmployee;
