    // Use state values directly for guards
    if (isLoadingPreviousRef.current || firstPageInMemory <= 0 || !hasPreviousState) {
        console.log(`[LOAD_PREVIOUS_SKIPPED] isLoadingPreviousRef: ${isLoadingPreviousRef.current}, FirstPageInMemory: ${firstPageInMemory}, HasPreviousState: ${hasPreviousState}`);
        // if(hasPreviousState && firstPageInMemory <=0) setHasPreviousState(false); // If at page 0, ensure hasPrevious is false.
        return Promise.resolve(); 
    }
    return fetchPreviousEmployeesAPI();
  };


  return (
    <>
      <style>{styles}</style>
      <div className="employee-list-container">
        <h1>Employee Directory</h1>
        <InfiniteScroll
          dataLength={totalItemsFetchedSoFar} 
          next={loadNextEmployees}
          hasMore={hasNextState} 
          loader={<div className="loader"><h4>Loading more employees...</h4></div>}
          endMessage={
            !hasNextState && employees.length > 0 && (
              <p style={{ textAlign: 'center' }} className="end-message">
                <b>You've reached the end of the list!</b>
              </p>
            )
          }
          pullDownToRefresh={hasPreviousState} 
          refreshFunction={loadPreviousEmployees}
          pullDownToRefreshContent={
            <h3 style={{ textAlign: 'center' }} className="pull-down-message">&#8595; Pull down to load previous</h3>
          }
          releaseToRefreshContent={
            <h3 style={{ textAlign: 'center' }} className="pull-down-message">&#8593; Release to load previous</h3>
          }
        >
          {employees.map(employee => (
            <div key={employee.id} className="employee-card">
              <h3>{employee.firstName} {employee.lastName}</h3>
              <p><strong>ID:</strong> {employee.id}</p>
              <p><strong>Birth Date:</strong> {employee.birthDate ? new Date(employee.birthDate).toLocaleDateString() : 'N/A'}</p>
            </div>
          ))}
        </InfiniteScroll>
        {!isLoadingNext && !isLoadingPrevious && employees.length === 0 && (
          <p className="no-employees">
            {!hasNextState && !hasPreviousState ? "No employees found." : "Scroll or pull down to load data."}
          </p>
        )}
      </div>
    </>
  );
}

export default EmployeeList;
